import ExplorableObject from "../../src/core/ExplorableObject.js";
import execute from "../../src/eg/execute.js";
import * as ops from "../../src/eg/ops.js";
import assert from "../assert.js";

describe("execute", () => {
  it("can execute", async () => {
    // Match the array representation of code generated by the parser.
    const parsed = [
      [ops.get, "greet"],
      [ops.get, "name"],
    ];
    const scope = new ExplorableObject({
      async greet(name) {
        return `Hello ${name}`;
      },
    });
    const graph = new ExplorableObject({
      name: "world",
    });
    const result = await execute(parsed, scope, graph);
    assert.equal(result, "Hello world");
  });

  it.skip("can use `this` to reference the current graph", async () => {
    const parsed = ["this"];
    const scope = new ExplorableObject({});
    const graph = new ExplorableObject({});
    const result = await execute(parsed, scope, graph);
    assert.equal(result, graph);
  });
});
